CREATE TABLE "managers" ("playerID_id" VARCHAR(255) NOT NULL, "yearID" INTEGER, "teamID" VARCHAR(255) NOT NULL, "lgID" VARCHAR(255) NOT NULL, "inseason" INTEGER, "G" INTEGER, "W" INTEGER, "L" INTEGER, "rank" INTEGER, "plyrMgr" VARCHAR(255), PRIMARY KEY ("playerID_id", "lgID", "lgID", "yearID"), FOREIGN KEY ("playerID_id") REFERENCES "master" ("playerID"));
CREATE TABLE "awards_players" ("playerID_id" VARCHAR(255) NOT NULL, "awardID" VARCHAR(255), "yearID" INTEGER, "lgID" VARCHAR(255), "tie" VARCHAR(255), "notes" VARCHAR(255), PRIMARY KEY ("playerID_id", "awardID", "yearID"), FOREIGN KEY ("playerID_id") REFERENCES "master" ("playerID"));
CREATE TABLE "fielding_post" ("playerID_id" VARCHAR(255) NOT NULL, "yearID" INTEGER, "teamID" VARCHAR(255), "lgID" VARCHAR(255), "round" VARCHAR(255), "pos" VARCHAR(255), "G" INTEGER, "GS" INTEGER, "InnOuts" INTEGER, "PO" INTEGER, "A" INTEGER, "E" INTEGER, "DP" INTEGER, "TP" INTEGER, "PB" INTEGER, "SB" INTEGER, "CS" INTEGER, PRIMARY KEY ("playerID_id", "yearID", "round", "pos"), FOREIGN KEY ("playerID_id") REFERENCES "master" ("playerID"));
CREATE TABLE "awards_share_players" ("awardID" VARCHAR(255), "yearID" INTEGER, "lgID" VARCHAR(255), "playerID_id" VARCHAR(255) NOT NULL, "pointsWon" INTEGER, "pointsMax" INTEGER, "votesFirst" INTEGER, PRIMARY KEY ("awardID", "yearID", "lgID", "playerID_id"), FOREIGN KEY ("playerID_id") REFERENCES "master" ("playerID"));
