CREATE TABLE "allstar_full" ("playerID_id" VARCHAR(255) NOT NULL, "yearID" INTEGER, "gameNum" INTEGER, "gameID" VARCHAR(255), "teamID" VARCHAR(255), "lgID" VARCHAR(255), "GP" INTEGER, "startingPos" INTEGER, PRIMARY KEY ("playerID_id", "yearID", "gameNum"), FOREIGN KEY ("playerID_id") REFERENCES "master" ("playerID"));
