CREATE TABLE "pitching_post" ("playerID_id" VARCHAR(255) NOT NULL, "yearID" INTEGER, "round" VARCHAR(255), "teamID" VARCHAR(255), "lgID" VARCHAR(255), "W" INTEGER, "L" INTEGER, "G" INTEGER, "GS" INTEGER, "CG" INTEGER, "SHO" INTEGER, "SV" INTEGER, "IPouts" INTEGER, "H" INTEGER, "ER" INTEGER, "HR" INTEGER, "BB" INTEGER, "SO" INTEGER, "BAOpp" REAL, "ERA" REAL, "IBB" INTEGER, "WP" INTEGER, "HBP" INTEGER, "BK" INTEGER, "BFP" INTEGER, "GF" INTEGER, "R" INTEGER, "SH" INTEGER, "SF" INTEGER, "GIDP" INTEGER, PRIMARY KEY ("playerID_id", "yearID", "round"), FOREIGN KEY ("playerID_id") REFERENCES "master" ("playerID"));
CREATE TABLE "salaries" ("yearID" INTEGER, "teamID" VARCHAR(255), "lgID" VARCHAR(255), "playerID_id" VARCHAR(255) NOT NULL, "salary" INTEGER, PRIMARY KEY ("yearID", "teamID", "playerID_id"), FOREIGN KEY ("playerID_id") REFERENCES "master" ("playerID"));
CREATE TABLE "schools" ("schoolID" VARCHAR(255) PRIMARY KEY, "name_full" VARCHAR(255), "city" VARCHAR(255), "state" VARCHAR(255), "country" VARCHAR(255));
CREATE TABLE "college_playing" ("playerID_id" VARCHAR(255) NOT NULL, "schoolID_id" VARCHAR(255) NOT NULL, "yearID" INTEGER, PRIMARY KEY ("playerID_id", "schoolID_id", "yearID"), FOREIGN KEY ("playerID_id") REFERENCES "master" ("playerID"), FOREIGN KEY ("schoolID_id") REFERENCES "schools" ("schoolID"));
CREATE TABLE "series_post" ("yearID" INTEGER, "round" VARCHAR(255), "teamIDwinner" VARCHAR(255), "lgIDwinner" VARCHAR(255), "teamIDloser" VARCHAR(255), "lgIDloser" VARCHAR(255), "wins" INTEGER, "losses" INTEGER, "ties" INTEGER, PRIMARY KEY ("yearID", "round"));
