CREATE TABLE "teams" ("yearID" INTEGER, "lgID" VARCHAR(255), "teamID" VARCHAR(255), "franchID_id" VARCHAR(255) NOT NULL, "divID" VARCHAR(255), "Rank" INTEGER, "G" INTEGER, "Ghome" INTEGER, "W" INTEGER, "L" INTEGER, "DivWin" VARCHAR(255), "WCWin" VARCHAR(255), "LgWin" VARCHAR(255), "WSWin" VARCHAR(255), "R" INTEGER, "AB" INTEGER, "H" INTEGER, "2B" INTEGER, "3B" INTEGER, "HR" INTEGER, "BB" INTEGER, "SO" INTEGER, "SB" INTEGER, "CS" INTEGER, "HBP" INTEGER, "SF" INTEGER, "RA" INTEGER, "ER" INTEGER, "ERA" REAL, "CG" INTEGER, "SHO" INTEGER, "SV" INTEGER, "IPouts" INTEGER, "HA" INTEGER, "HRA" INTEGER, "BBA" INTEGER, "SOA" INTEGER, "E" INTEGER, "DP" INTEGER, "FP" REAL, "name" VARCHAR(255), "park_id" VARCHAR(255) NOT NULL, "attendance" INTEGER, "BPF" INTEGER, "PPF" INTEGER, "teamIDBR" VARCHAR(255), "teamIDlahman45" VARCHAR(255), "teamIDretro" VARCHAR(255), PRIMARY KEY ("yearID", "lgID", "teamID"), FOREIGN KEY ("franchID_id") REFERENCES "teams_franchises" ("franchID"), FOREIGN KEY ("park_id") REFERENCES "parks" ("name"));
